import React from 'react';
import {useSelector} from 'react-redux';

const AppointmentsList = ()=>{

    const appointments =useSelector((state)=>state.appointments);

    console.log(appointments);

    return(
        <div className="card mt-5">

            <div className="card-body">
                <h2 className="card-title text-center">Manage Your Appointments</h2>
                <div className="lista-citas">
                    {appointments.appointments.map(appointment=>(

                            <div className="media mt-3">
                            <div className="media-body">
                                <h3 className="mt-0"></h3>
                                <p className="card-text"><span>Owner's name:</span></p>
                                <p className="card-text"><span>Date:</span></p>
                                <p className="card-text"><span>Hour:</span>  </p>
                                <p className="card-text"><span>Symptoms:</span> <br /> </p>
                                <button 
                                    className="btn btn-danger">Delete &times;
                                </button>
                            </div>
                            </div>


                    ))}
                </div>

            </div>
            
        </div>

    );
}

export  default AppointmentsList;
